import React, { useEffect, useState } from 'react';
import './home.css'
import axios from 'axios';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';

import Loading from '../Loading';
import { setLoadingGlobal } from '../../store/slices/isLoading.slice';

import ContentLoader, { Facebook } from 'react-content-loader'




const Home = () => {

    const dispatch = useDispatch()

    const isLoading = useSelector(state => (state.isLoading));
    const [temas, setTemas] = useState([]);
    const setLoadGlobal = (state) => dispatch(setLoadingGlobal(state))
    const [prueba, setPrueba] = useState(true)



    useEffect(() => {
        setLoadGlobal(true)
        axios.get("http://localhost/foro/foro/server/temas.php")
            .then(res => {
                setTemas(res.data)
                setTimeout(() => {
                    setLoadGlobal(false)
                }, 2000);
            })
            .catch(error => console.log(error))
    }, [])

    const MyLoader = () => (
        <div className='contenedorSkeleton'>
        <ContentLoader
            width={170}
            height={215}
            viewBox="0 0 175 215"
            backgroundColor="#f0f0f0"
            foregroundColor="#dedede"
            // {...props}
        >
            <rect x="40" y="10" rx="10" ry="10" width="100" height="100" />
            <rect x="35" y="160" rx="10" ry="10" width="125" height="15" />
            <rect x="35" y="180" rx="10" ry="10" width="125" height="15" />
            
        </ContentLoader>
        </div>
    )
    const MyFacebookLoader = () => <Facebook />

    return (
        isLoading ? <Loading />
            :
            <div className='general-content'>
                <div className='containerCards'>
                    {temas.map(data => {
                        return (<Link to={`/temas/${data.temas}/${data.id}`} key={data.id}>
                            <div className='card'>
                                <div>
                                    <img src={data.URL} />
                                </div>
                                <h2>{data.temas}</h2>
                            </div></Link>)
                    })}
                    {MyLoader()}
                </div>
            </div>
    );
};

export default Home;
