import axios from 'axios';
import React, { useState } from 'react';
import './pruebasStyles.css';
// import { Navigate, useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';




import userLogin from './../img/Saly-13.png';
import BtnLoad from './BtnLoad';
import { setLoadingGlobal } from '../store/slices/isLoading.slice';
import Login from './Login/Login';
import CheckRegister from './Login/CheckRegister';
import Loading from './Loading';


const PruebasStyles = () => {

    const [alert, setAlert] = useState(false)
    const [isLoading, setIsLoading] = useState(false)
    const [msgErr, setMsgErr] = useState(false)
    const [login, setLogin] = useState(false)
    const [emailErr, setEmailErr] = useState(false)
    const [check, setCheck] = useState(false)

    // const navigate = useNavigate();

    const data = e => {
        e.preventDefault()
        setIsLoading(true)
        setMsgErr(false)
        setEmailErr(false)
        setAlert(false)

        let user = e.target.username.value
        let email = e.target.email.value
        let password = e.target.password.value
        let confirmPassword = e.target.confirmPassword.value

        if (password !== confirmPassword) {
            // setIsLoading(false)
            // return setAlert(true)
            return console.log('funciona')
        }

        axios.post("http://localhost/foro/foro/server/usuarios.php", { nombre: user, contraseña: password, email })
            .then(res => {
                setCheck(true)
                console.log('Registro exitoso')
            })
            .catch(error => {
                setTimeout(() => {
                    setMsgErr(true)
                    setIsLoading(false)

                    if (error.response.data.mesage == 'El email ya esta en uso') {
                        setEmailErr(true)
                    }
                }, 2000);


                console.log(error.response.data.mesage)
            });
    }

    if (check) {
        return <CheckRegister />
    }

    if (login) {
        return (<Login />)
    }

    return (
        <div className='background'>
            <div className='contain'>
                <div className='contain2'>
