                                <h3>Registrate</h3>
                                <div>
                                    <input className='inputInicio' type="text" name="username" placeholder='Nombre de usuario' required></input>
                                </div>
                                <div>
                                    <input className={emailErr ? 'marginInput error-email' : 'inputInicio'} type="email" name="email" placeholder='Email' required></input>
                                    {emailErr && <p className='msg-error'>Email ya esta en uso</p>}
                                </div>
                                <div>
                                    <input className='inputInicio' type="password" name="password" placeholder='Contraseña' required></input>
                                </div>
                                <div>
