import React, { useEffect, useState } from 'react';
import './home.css'
import axios from 'axios';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';

import Loading from '../Loading';
import { setLoadingGlobal } from '../../store/slices/isLoading.slice';

import ContentLoader, { Facebook } from 'react-content-loader'




const Home = () => {

    const dispatch = useDispatch()

    const isLoading = useSelector(state => (state.isLoading));
    const [temas, setTemas] = useState([]);
    const setLoadGlobal = (state) => dispatch(setLoadingGlobal(state))
    const [prueba, setPrueba] = useState(true)



    useEffect(() => {
        setLoadGlobal(true)
        axios.get("http://localhost/foro/foro/server/temas.php")
            .then(res => {
                setTemas(res.data)
                setTimeout(() => {
                    setLoadGlobal(false)
                }, 2000);
            })
            .catch(error => console.log(error))
    }, [])

    const MyLoader = () => (
        <ContentLoader speed={2} 
        viewBox="0 0 380 70" width={476} height={124}>
          {/* Only SVG shapes */}    
          <rect x="0" y="0" rx="5" ry="5" width="70" height="70" />
          <rect x="80" y="17" rx="4" ry="4" width="300" height="13" />
          <rect x="80" y="40" rx="3" ry="3" width="250" height="10" />
        </ContentLoader>
      )
    const MyFacebookLoader = () => <Facebook />

    return (
        isLoading ? <Loading />
            :
            <div className='general-content'>
                <div className='containerCards'>
                    {temas.map(data => {
                        return (<Link to={`/temas/${data.temas}/${data.id}`} key={data.id}>
                            <div className='card'>
                                <div>
                                    <img src={data.URL} />
                                </div>
                                <h2>{data.temas}</h2>
                            </div></Link>)
                    })}
                {MyLoader()} 
                </div>
            </div>
    );
};

export default Home;
